        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
      ],
    }
    this.song=[];
    this.bpm=120;
  }

   playSynth=(i,j)=>  {
      var temp = this.state.synthMatrix;
      temp[i][j] = !temp[i][j];
      this.setState({synthMatrix : temp});
      this.loadSequence();
  }

 playDrums=(i,j)=>  {
    var temp = this.state.drumsMatrix;
    temp[i][j] = !temp[i][j];
    this.setState({drumsMatrix : temp});
    this.loadSequence();
  }

   playBass=(i,j)=>  {
      var temp = this.state.bassMatrix;
      temp[i][j] = !temp[i][j];
      this.setState({bassMatrix : temp});
      this.loadSequence();
  }

  loadSequence() {
    for (var i=0;i<16;i++){

      let song=[];

      //drums
      let drums=[];
      if (this.state.drumsMatrix[0][i]){drums.push(2);}
      if (this.state.drumsMatrix[1][i]){drums.push(17);}
      if (this.state.drumsMatrix[2][i]){drums.push(56);}
      if (this.state.drumsMatrix[3][i]){drums.push(35);}

      //synth
     let synths=[837, [], 2.5];
      if (this.state.synthMatrix[0][i]){synths[1].push(50+12)}
      if (this.state.synthMatrix[1][i]){synths[1].push(50+11)}
      if (this.state.synthMatrix[2][i]){synths[1].push(50+9)}
      if (this.state.synthMatrix[3][i]){synths[1].push(50+7)}
      if (this.state.synthMatrix[4][i]){synths[1].push(50+5)}
      if (this.state.synthMatrix[5][i]){synths[1].push(50+4)}
      if (this.state.synthMatrix[6][i]){synths[1].push(50+2)}
      if (this.state.synthMatrix[7][i]){synths[1].push(50+0)}

      // bass
      let bajos=[434, [], 2.5];
       if (this.state.bassMatrix[0][i]){bajos[1].push(30+2)}
       if (this.state.bassMatrix[1][i]){bajos[1].push(30+1)}
       if (this.state.bassMatrix[2][i]){bajos[1].push(30+9)}
       if (this.state.bassMatrix[3][i]){bajos[1].push(30+7)}
       if (this.state.bassMatrix[4][i]){bajos[1].push(30+5)}
       if (this.state.bassMatrix[5][i]){bajos[1].push(30+4)}
       if (this.state.bassMatrix[6][i]){bajos[1].push(30+2)}
       if (this.state.bassMatrix[7][i]){bajos[1].push(30+0)}

      //push everything
      song.push(drums, [synths, bajos]);

      this.song[i]=song;
    }
  }

  playLoop(){
    this.loadSequence();
    this.setState({looping: true});
    this.midiSounds.startPlayLoop(this.song, this.bpm, 1/16);
  }

  stopLoop(){
    this.midiSounds.stopPlayLoop();
    this.setState({looping: false});
  }

  componentDidMount(){
    this.playLoop();
  }

  changeGrid=(grid)=>{
    this.setState({currentGrid: grid});
  }

  loadSong=(drums, bass, synth)=> {
    this.setState({
      drumsMatrix: drums, 
      bassMatrix: bass, 
      synthMatrix: synth
    });
    this.loadSequence();
  }

  clickEffect=()=>{
    document.addEventListener("mouseover", function(e){
      var gradient = document.getElementById('cover');
