import React, { Component } from 'react';
import MIDISounds from 'midi-sounds-react';

class DrumsGrid extends Component {

  constructor(props){
    super(props);
    this.state = {

    }

  }

 	playDrums(i, j) {
 		this.props.playDrums(i,j);
 	}

	createTable () {
		let table = []
		let key=0;
		// Outer loop to create rows
		for (let i = 0; i < 8; i++){
			let children = []
			// Inner loop to make td elements
			for (let j = 0; j < 16; j++){
				children.push(<td key={key} value={this.props.drumMatrix[i][j]} onClick={() => this.playDrums(i,j)}></td>)
				key++;
			}
			table.push(<tr key={key}>{children}</tr>)
		}
		return table;
	}

  render() {
    return (
      <div>

		<table>
			<tbody>
				{this.createTable()}
			</tbody>
		</table>

      </div>
    );
  }
}

export default DrumsGrid;
