import React, { Component } from 'react';
import './App.css';
import MIDISounds from 'midi-sounds-react';

import SynthGrid from './components/SynthGrid';
import DrumsGrid from './components/DrumsGrid';
import BassGrid from './components/BassGrid';

class App extends Component {

  constructor(){
    super();
    this.state = {
      currentGrid: '',
      drumMatrix : [
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
      ],   
      synthMatrix : [
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
      ], 
      bassMatrix : [
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
        [false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
      ],         
    }
  }

  createTable () {
    let table = []
    let key=0;
    // Outer loop to create rows
    for (let i = 0; i < 8; i++){
      let children = []
      // Inner loop to make td elements
      for (let j = 0; j < 16; j++){
        children.push(<td key={key} value={this.props.drumMatrix[i][j]} onClick={() => this.playDrums(i,j)}></td>)
        key++;
      }
      table.push(<tr key={key}>{children}</tr>)
    }
    return table;
  }

   playSynth=(i,j)=>  {
      let note;
      switch(i){
        case 0:
          note=60;
          break;
        case 1:
          note=62;
          break;
        case 2:
          note=64;
          break;
        case 3:
          note=65;
          break;
        case 4:
          note=67;
          break;
        case 5:
          note=69;
          break;
        case 6:
          note=71;
          break;
        case 7:
          note=72;
          break;
      }

      this.midiSounds.playChordNow(3, [note], 2.5);
      var temp = this.state.synthMatrix;
      temp[i][j] = !temp[i][j];
      this.setState({synthMatrix : temp});

  }

  render() {
    return (
      <div>
        <MIDISounds ref={(ref) => (this.midiSounds = ref)} appElementName="root" instruments={[3]} />
        <SynthGrid createTable={this.createTable} synthMatrix={this.state.synthMatrix} playSynth={this.playSynth}/>

      </div>
    );
  }
}

export default App;
