import React, { Component } from 'react';

class ToggleMenu extends Component {

    constructor(props){
        super(props);
        this.state={
            savingSong: false,
        }
    }

    changeGrid (grid){
        this.props.changeGrid(grid);
    }

    saveSong (){
        this.setState({savingSong: true});

    }

    openSong(){

    }

    render() {
        return (
            <div>
                <nav class="nav-extended">
                    <div class='tabs tabs-transparent'>
                        <p class='tab' onClick={()=>this.changeGrid('drums')}><a>drums</a></p>
                        <p class='tab' onClick={()=>this.changeGrid('synth')}><a>synth</a></p>
                        <p class='tab' onClick={()=>this.changeGrid('bass')}><a>bass</a></p>
                        <p class='tab' onClick={()=>this.saveSong()}><a>save</a></p>
                        <p class='tab' onClick={()=>this.openSong()}><a>open</a></p>
                    </div>
                </nav>
                {
                    this.state.savingSong
                    ? console.log('savingSong is true')
                    : null
                }
            </div>
        );
    }
}


export default ToggleMenu;
