import React, { Component } from 'react';
import MIDISounds from 'midi-sounds-react';

class SynthGrid extends Component {

  constructor(){
    super();
    this.state = {
   		matrix : [
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
		]
    };
    this.melodies=[];
  }

  playC(i,j) {
    this.midiSounds.playChordNow(3, [60], 2.5);
	var temp = this.state.matrix;
	  console.log(temp[i][j]);
	temp[i][j] = !temp[i][j];

	this.setState({matrix : temp});
	  console.log(temp[i][j]);
  }

   playD() {
    this.midiSounds.playChordNow(3, [62], 2.5);
  }

  storeSequence(){
  	for (let i=0; i<16; i++){
  		var notes=[];
  		if(this.state.matrix[0][i]){notes.push(62);}
  		var melody=[notes, []];
  		this.melodies[i]=melody;
  	}

  }

  playLoop(){
  	this.storeSequence();
  	this.midiSounds.startPlayLoop(this.melody, 120, 1/16);
  }

	createTable() {
		let table = []
		// Outer loop to create rows
		for (let i = 0; i < 8; i++){
			let children = []
			// Inner loop to make td elements
			for (let j = 0; j < 16; j++){
				children.push(<td value={this.state.matrix[i][j]} onClick={()=>this.playC(i,j)}></td>)
			}
			table.push(<tr>{children}</tr>)
		}
		return table;
	}

  render() {
    return (
      <div>
        <MIDISounds ref={(ref) => (this.midiSounds = ref)} appElementName="root" instruments={[3]} />

		<table>
			{this.createTable()}
		</table>

		<button onClick={()=>this.playLoop}>Play loop</button>

      </div>
    );
  }
}

export default SynthGrid;
