import React, { Component } from 'react';
import MIDISounds from 'midi-sounds-react';

class SynthGrid extends Component {

	constructor(){
		super();
		this.state = {
			matrix : [
			[true, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, true, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, true, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			[false, false, false, false, false, false, false, false, false, false, false, false, false, false, false, false],
			]
		};
		this.melodies=[];
	}

	playNote(i,j) {

		let note = 60 + i;
		console.log(note);
		this.midiSounds.playChordNow(3, [60], 2.5);

		var temp = this.state.matrix;
		console.log(temp[i][j]);
		temp[i][j] = !temp[i][j];

		this.setState({matrix : temp});
		console.log(temp[i][j]);
	}

	storeSequence(){
		for (let i=0; i<16; i++){
			var notes=[];
			if(this.state.matrix[0][i]){notes.push(62);}
			var melody=[notes, []];
			this.melodies[i]=melody;
		}
	}

	playLoop(){

	}

	createTable() {
		let table = []
		let key=0;
		// Outer loop to create rows
		for (let i = 0; i < 8; i++){
			let children = []
			// Inner loop to make td elements
			for (let j = 0; j < 16; j++){
				children.push(<td key={key} value={this.state.matrix[i][j]} onClick={()=>this.playNote(i,j)}></td>)
				key++;
			}
			table.push(<tr key={key}>{children}</tr>)
		}
		return table;
	}

	render() {
		return (
			<div>

				<table>
					<tbody>
						{this.createTable()}
					</tbody>
				</table>

				<button onClick={()=>this.playLoop}>Play loop</button>

			</div>
			);
	}
}

export default SynthGrid;
