import React, { Component } from 'react';
import MIDISounds from 'midi-sounds-react';

class SynthGrid extends Component {

  constructor(props){
    super(props);
    this.state = {

    }

  }

 	playSynth(i, j) {
 		this.props.playSynth(i,j);
 	}

	createTable = () => {
		let table = []
		let key=0;
		// Outer loop to create rows
		for (let i = 0; i < 8; i++){
			let children = []
			// Inner loop to make td elements
			for (let j = 0; j < 16; j++){
				children.push(<td key={key} value={this.props.matrix[i][j]} onClick={() => this.playSynth(i,j)}></td>)
				key++;
			}
			table.push(<tr key={key}>{children}</tr>)
		}
		return table;
	}

  render() {
    return (
      <div>
        <MIDISounds ref={(ref) => (this.midiSounds = ref)} appElementName="root" instruments={[3]} />

		<table>
			<tbody>
				{this.createTable()}
			</tbody>
		</table>

      </div>
    );
  }
}

export default SynthGrid;
