import React, { Component } from 'react';

class SynthGrid extends Component {

  constructor(props){
    super(props);
    this.state = {

    }
  }

 	playSynth(i, j) {
 		this.props.playSynth(i,j);
 	}

	createTable () {
		let table = []
		let key=0;
		// Outer loop to create rows
		for (let i = 0; i < 8; i++){
			let children = []
			// Inner loop to make td elements
			for (let j = 0; j < 8; j++){
				children.push(<td 
          		className={this.props.synthMatrix[i][j] 
         		? 'synth neon' 
 				: '' 
 				} 
				key={key} value={this.props.synthMatrix[i][j]} onClick={()=>this.playSynth(i,j)}>
				<div className='filledcircle'></div>
			</td>)
			key++;
		}
			table.push(<tr key={key}>{children}</tr>)
		}
		return table;
	}

  render() {
    return (
      <div>

		<table>
			<tbody>
				{this.createTable()}
			</tbody>
		</table>

      </div>
    );
  }
}

export default SynthGrid;
